func:
const ComponentId = env.get("Component-ID");

const actualPower = parseInt(flow.get("bat").toFixed(0));
const actualPowerkW = actualPower / 1000;

const maxCapacity = flow.get("maxCapacity");
const actualCapacity = flow.get("actualCapacity");
var actualSoc = actualCapacity / maxCapacity * 100;
actualSoc = parseInt(actualSoc.toFixed(0));

var statusColor = "yellow";
var statusShape = "ring";
if(actualPower < 0)
    statusColor = "red";
if(actualPower > 0)
    statusColor = "green";
if(actualSoc > 0)
    statusShape = "dot";

node.status({
        fill:  statusColor,
        shape: statusShape,
        text:  actualPowerkW.toFixed(1)+"kW " + actualSoc + "%"
    });

return [
    [
        {
            topic: "openWB/set/bat/" + ComponentId + "/get/imported",
            payload: parseFloat(flow.get("WhImported").toFixed(3))
        },
        {
            topic: "openWB/set/bat/" + ComponentId + "/get/exported",
            payload: parseFloat(flow.get("WhExported").toFixed(3))
        },
        {
            topic: "openWB/set/bat/" + ComponentId + "/get/power",
            payload: actualPower
        },
        {
            topic: "openWB/set/bat/" + ComponentId + "/get/soc",
            payload: actualSoc
        }
    ]
]

const range = flow.get("maxPower");
const actualCapacity = flow.get("actualCapacity");
const maxCapacity = flow.get("maxCapacity");

msg.payload = msg.payload * (2*range) - range;
if( actualCapacity == 0 ){
    msg.payload = Math.max(msg.payload,0);
} else if( actualCapacity == maxCapacity ){
    msg.payload = Math.min(msg.payload,0);
}
return msg;

var actualCapacity = flow.get("actualCapacity");
const maxCapacity = flow.get("maxCapacity");

actualCapacity = Math.min(
    Math.max(
        actualCapacity + msg.payload,
        0
        ),
        maxCapacity
    );

flow.set("actualCapacity", actualCapacity);
msg.payload = actualCapacity;

return msg;

node.status({
        text: flow.get("leistungLP1")+"/"+flow.get("leistungLP2")+"/"+flow.get("leistungLP3")+"/"+flow.get("hausverbrauch")+"/"+flow.get("pv")+"/"+flow.get("bat")
    });


msg.payload =  flow.get("leistungLP1") + flow.get("leistungLP2") + flow.get("leistungLP3") + flow.get("hausverbrauch") + flow.get("pv") + flow.get("bat")
return msg;

const ComponentId = env.get("Component-ID");

const actualPower = parseInt(flow.get("evu").toFixed(0));
const actualPowerkW = actualPower / 1000;
const actualCurrent = flow.get("evu_current");
const voltageL1 = flow.get("spannungL1");
const voltageL2 = flow.get("spannungL2");
const voltageL3 = flow.get("spannungL3");
const powerFactorL1 = flow.get("leistungsfaktorL1");
const powerFactorL2 = flow.get("leistungsfaktorL2");
const powerFactorL3 = flow.get("leistungsfaktorL3");

var statusColor = "green";
var statusShape = "dot";
if(actualPower > 0)
    statusColor= "red";

node.status({
        fill: statusColor,
        shape: statusShape,
        text:actualPowerkW.toFixed(1)+"kW, "+flow.get("evu_current")
    });

return [
    [
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/imported",
            payload: parseFloat(flow.get("WhImported").toFixed(3))
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/exported",
            payload: parseFloat(flow.get("WhExported").toFixed(3))
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/power",
            payload: parseInt(flow.get("evu").toFixed(0))
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/currents",
            payload: actualCurrent
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/voltages",
            payload: [voltageL1, voltageL2, voltageL3]
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/powers",
            payload: [voltageL1 * actualCurrent[0] * powerFactorL1, voltageL2 * actualCurrent[1] * powerFactorL2, voltageL3 * actualCurrent[2] * powerFactorL3]
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/power_factors",
            payload: [powerFactorL1, powerFactorL2, powerFactorL3]
        },
        {
            topic: "openWB/set/counter/" + ComponentId + "/get/frequency",
            payload: parseFloat(flow.get("frequency"))
        }
   ]
]

const current_others = (flow.get("pv") + flow.get("bat")) / 230 / 3
const currentP1 = current_others + flow.get("hausverbrauch_p1") / 230 + flow.get("current_p1_LP1") + flow.get("current_p1_LP2") + flow.get("current_p1_LP3")
const currentP2 = current_others + flow.get("hausverbrauch_p2") / 230 + flow.get("current_p2_LP1") + flow.get("current_p2_LP2") + flow.get("current_p2_LP3")
const currentP3 = current_others + flow.get("hausverbrauch_p3") / 230 + flow.get("current_p3_LP1") + flow.get("current_p3_LP2") + flow.get("current_p3_LP3")

msg.payload = [parseFloat(currentP1.toFixed(2)), parseFloat(currentP2.toFixed(2)), parseFloat(currentP3.toFixed(2))]
return msg;

const LPNumber = env.get("Component-ID");

var chargestat = 0;
if( flow.get("Lp_Strom1") > 0 || flow.get("Lp_Strom2") > 0 || flow.get("Lp_Strom3") > 0 ){
    chargestat = 1;
}

var statusColor = "red";
var statusShape = "ring";
if (flow.get("Lp_Stecker") == 1){
    statusColor = "green"
    if (chargestat == 1){
        statusShape = "dot";
    }
}

const actualPower = parseInt(flow.get("Lp_Ladeleistung").toFixed(0));
const actualPowerkW = actualPower / 1000;
const phasesInUse = flow.get("Lp_Phasen");
const phasesToUse = flow.get("Lp_Phasenumschaltung");
const phasesEVmax = flow.get("Lp_EV_Phasen");
const currentL1 = flow.get("Lp_Strom1");
const currentL2 = flow.get("Lp_Strom2");
const currentL3 = flow.get("Lp_Strom3");
const volatageL1 = flow.get("Lp_Spannung1");
const volatageL2 = flow.get("Lp_Spannung2");
const volatageL3 = flow.get("Lp_Spannung3");
const powerFactorL1 = flow.get("Lp_Leistungsfaktor1");
const powerFactorL2 = flow.get("Lp_Leistungsfaktor2");
const powerFactorL3 = flow.get("Lp_Leistungsfaktor3");

node.status({
        fill: statusColor,
        shape: statusShape,
        text:actualPowerkW.toFixed(1)+"kW "+phasesInUse+"P("+phasesEVmax+"/"+phasesToUse+")"
    });

return [
    [
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/currents",
            payload: [currentL1, currentL2, currentL3]
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/voltages",
            payload: [volatageL1, volatageL2, volatageL3]
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/power",
            payload: actualPower
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/powers",
            payload: [volatageL1 * currentL1 * powerFactorL1, volatageL2 * currentL2 * powerFactorL2, volatageL3 * currentL3 * powerFactorL3]
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/power_factors",
            payload: [powerFactorL1, powerFactorL2, powerFactorL3]
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/frequency",
            payload: flow.get("Lp_Netzfrequenz")
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/imported",
            payload: parseFloat(flow.get("Lp_Zähler_laden").toFixed(3))
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/exported",
            payload: parseFloat(flow.get("Lp_Zähler_entladen").toFixed(3))
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/plug_state",
            payload: flow.get("Lp_Stecker")
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/charge_state",
            payload: chargestat
        },
        {
            topic: "openWB/set/chargepoint/" + LPNumber + "/get/phases_in_use",
            payload: phasesInUse
        }
    ]
]

const phasesEVmax = flow.get("Lp_EV_Phasen");
const phasesLPmax = flow.get("Lp_Phasenumschaltung");
var phasesInUse = phasesEVmax;
if(phasesEVmax > phasesLPmax){
    phasesInUse = phasesLPmax;
}

var phaseCurrents = [0,0,0];

phaseCurrents[0] = msg.payload;
if( phasesInUse > 1 )
    phaseCurrents[1] = msg.payload;
if( phasesInUse > 2 )
    phaseCurrents[2] = msg.payload;

flow.set("Lp_Strom1", phaseCurrents[0]);
flow.set("Lp_Strom2", phaseCurrents[1]);
flow.set("Lp_Strom3", phaseCurrents[2]);
flow.set("Lp_Phasen", phasesInUse);

return msg;

const componentId = env.get("Component-ID");
const phasesInUse = flow.get("Lp_Phasen");
const phase1 = parseInt(env.get("Phase1"));

var phaseCurrents = [0,0,0];

switch (phase1) {
    case 1:
        phaseCurrents[0] = msg.payload;
        if( phasesInUse > 1 )
            phaseCurrents[1] = msg.payload;
        if( phasesInUse > 2 )
            phaseCurrents[2] = msg.payload;
        break;
    case 2:
        phaseCurrents[1] = msg.payload;
        if( phasesInUse > 1 )
            phaseCurrents[2] = msg.payload;
        if( phasesInUse > 2 )
            phaseCurrents[0] = msg.payload;
        break;
    case 3:
        phaseCurrents[2] = msg.payload;
        if( phasesInUse > 1 )
            phaseCurrents[0] = msg.payload;
        if( phasesInUse > 2 )
            phaseCurrents[1] = msg.payload;
        break;
}

var evuPhaseCurrentMsgs = [
    {
        "topic": "/chargepoint/" + componentId + "/get/current_p1",
        "payload": phaseCurrents[0]
    },
    {
        "topic": "/chargepoint/" + componentId + "/get/current_p2",
        "payload": phaseCurrents[1]
    },
    {
        "topic": "/chargepoint/" + componentId + "/get/current_p3",
        "payload": phaseCurrents[2]
    }
];

return [evuPhaseCurrentMsgs];
